angular.module('infinite-scroll', [])
    .directive('infiniteScroll', [ "$window", function ($window) {
        return {
            scope: {
        infiniteScroll: '&',
        treshold: '=',
        canLoad: '=',
        infiniteScrollInvert: '='
      },
            link:function (scope, element, attrs) {
                var offset = parseInt(attrs.threshold) || 0;
                var e = element[0];

                element.bind('scroll', function () {
                    console.log('e.scrollTop>>'+e.scrollTop);
                    console.log('e.offsetHeight>>'+e.offsetHeight);
                    console.log('e.scrollHeight>>'+e.scrollHeight);
                    console.log('offset>>'+offset);
                    console.log('e.scrollTop + e.offsetHeight>>'+(e.scrollTop + e.offsetHeight));

                    if (scope.$eval(attrs.canLoad)) {
                        if((!scope.infiniteScrollInvert && e.scrollTop + e.offsetHeight >= e.scrollHeight - offset) ||
                            (scope.infiniteScrollInvert && e.scrollTop + e.offsetHeight <= e.offsetHeight + offset))
                        scope.$apply(attrs.infiniteScroll);
                    }
                });
            }
        };
    }]);